        this.hiddendivpointer=(i+1>ceiling-1)? 0 : i+1
        this.hiddendiv.innerHTML=this.content[this.hiddendivpointer]
        this.animateup()
        }
        }

        pausescroller.getCSSpadding=function(tickerobj){ //get CSS padding value, if any
