--><input type=hidden id=destinationLink name=destinationLink><script type=text/javascript>
var rateJobPanel = null;
var reportJobPanel = null;
var reportJobPanelReported = null;
var connectPanel = null;

var init = function () {
    var loginPanel;
    var validLogin;
    var submitAfterLogin;
    var isAuthenticated = "false";
    var jobReportedDate = "";

    var r = Math.floor(Math.random()*10000);
    var joinNetworkHref = "/jobsearch/servlet/JobSearch?op=327&dockey=xml/c/9/c99c43e659e24135469fc9075265adc4&source=&currdoc=1&queryString=&diceID=10372285&joinNetwork=1&r=" + r;
    var rateJobHref ="/jobsearch/servlet/JobSearch?op=306&dockey=xml/c/9/c99c43e659e24135469fc9075265adc4&source=&currdoc=1&queryString=&r=" + r;
    var reportJobHref ="/jobsearch/servlet/JobSearch?op=2006&dockey=xml/c/9/c99c43e659e24135469fc9075265adc4&source=&currdoc=1&queryString=&r=" + r;

    // Load up any parameters we may have been handed and build the login panel.
    YAHOO.util.Event.onContentReady("mainBody", function() {











        YAHOO.util.Event.addListener("saveJob", "click", saveJobClicked);



        YAHOO.util.Event.addListener("saveJob2", "click", saveJobClicked);



        YAHOO.util.Event.addListener("rateJobJS", "click", rateThisJobClicked);


        YAHOO.util.Event.addListener("connectNetworkJS", "click", connectClicked);


        YAHOO.util.Event.addListener("joinNetworkJS", "click", joinThisNetworkClicked);



        YAHOO.util.Event.addListener("reportJobJS", "click", reportThisJobClicked);


        loginPanel = new YAHOO.widget.Panel("loginPanelDiv", { width:"450px", visible:false, fixedcenter : true, constraintoviewport: false, modal : true, y: 25 });
        loginPanel.hide();
        loginPanel.render();


        rateJobPanel = new YAHOO.widget.Overlay("rateJobPanel", { width: "16em",
            close : true,
            fixedcenter : false,
            context : ["rateJobJS", "tr", "br"],
            modal : false,
            visible : false,
            draggable : false,
            hideaftersubmit : true});
        rateJobPanel.render();
        if (false) {
            rateJobPanelShow();
        }



        reportJobPanel = new YAHOO.widget.Overlay("reportJobPanel", { width: "30em",
            close : true,
            fixedcenter : false,
            context : ["reportJobJS", "tr", "br"],
            modal : false,
            visible : false,
            draggable : false,
            hideaftersubmit : true});
        reportJobPanel.render();
        if (false) {
            reportJobPanelShow();
        }



        reportJobPanelReported = new YAHOO.widget.Overlay("reportJobPanelReported", { width: "27em",
            close : true,
            fixedcenter : false,
            context : ["reportJobJS", "tr", "br"],
            modal : false,
            visible : false,
            draggable : false,
            hideaftersubmit : true});
        reportJobPanelReported.render();



        connectPanel = new YAHOO.widget.Overlay("companyNetworkPanel", { width: "20em",
            close : true,
            fixedcenter : false,
            context : ["connectNetworkJS", "tr", "br"],
            modal : false,
            visible : false,
            draggable : false,
            hideaftersubmit : true});
        connectPanel.render();

    });

    // Load up the buttons on the login panel.
    YAHOO.util.Event.onContentReady("loginPanelDiv", function() {
        var oLinkButton1 = new YAHOO.widget.Button("loginPanelButton");
        var oLinkButton2 = new YAHOO.widget.Button("signUpNowPanelButton");

        YAHOO.util.Event.addListener("loginPanelButton", "click", logMeIn);
        YAHOO.util.Event.addListener("signUpNowPanelButton", "click", signUpNowClicked);
    });

    // Determine the rating and add it to the URL.
    function getRateUrl() {
        var rating;
        for (i = 1; i <= 3; i++) {
            if (document.getElementById("rating" + i).checked) {
                rating = document.getElementById('rating' + i).value;
            }
        }
        return rateJobHref + "&rating=" + rating;
    }

    // Handle a login button click. Set up some fields for later use and show the login panel.
    function loginClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);
        document.getElementById("destinationLink").value = "";
        document.getElementById("loginError").innerHTML = "";
        document.getElementById("loginPanelDiv").style.display = "block";
        loginPanel.show();
    }

    // Handle a hide job button click. Set up some fields for later use and call the processor.
    function hideJobClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);

        submitAfterLogin = 1;
        document.location.href = '/jobsearch/servlet/JobSearch?op=2003&dockey=xml/c/9/c99c43e659e24135469fc9075265adc4';
        return true;

    }

    // Handle a save job button click. Set up some fields for later use and either call the processor or show the login panel.
    function saveJobClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);

        if (isAuthenticated == "true") {
            var r = Math.floor(Math.random()*10000);
            document.location.href = "/jobsearch/servlet/JobSearch?op=303&pv=3&dk=xml/c/9/c99c43e659e24135469fc9075265adc4&jt=Quality+Engineer+-+Medical+Mfg+%28Direct%29&source=null&r=" + r;
        }
        else {
            submitAfterLogin = 1;
            document.getElementById("destinationLink").value = "/jobsearch/servlet/JobSearch?op=303&pv=3&dk=xml/c/9/c99c43e659e24135469fc9075265adc4&jt=Quality+Engineer+-+Medical+Mfg+%28Direct%29&source=null";
            document.getElementById("loginError").innerHTML = "";
            document.getElementById("loginPanelDiv").style.display = "block";
            loginPanel.show();
        }

    }

    // Handle a rate button click. Set up some fields for later use and either call the processor or show the login panel.
    // NOTE: This is when the rate button is clicked from within the YUI dropdown, not the rate button that displays the dropdown.
    function rateThisJobClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);

        if (isAuthenticated == "true") {
            rateJobPanelShow();
        }
        else {
            submitAfterLogin = 2;
            document.getElementById("destinationLink").value = getRateUrl();
            document.getElementById("loginError").innerHTML = "";
            document.getElementById("loginPanelDiv").style.display = "block";
            loginPanel.show();
        }

    }

    // Handle a join network button click. Set up some fields for later use and either call the processor or show the login panel.
    function joinThisNetworkClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);

        if (isAuthenticated == "true") {
            document.location.href = joinNetworkHref;
        }
        else {
            submitAfterLogin = 4;
            document.getElementById("destinationLink").value = joinNetworkHref;
            document.getElementById("loginError").innerHTML = "";
            document.getElementById("loginPanelDiv").style.display = "block";
            loginPanel.show();
        }

    }

    function connectClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);
        connectPanelShow();
    }

    // Handle a report job button click. Set up some fields for later use and either call the processor or  show the login panel.
    // NOTE: This is when the report job button is clicked from within the YUI dropdown, not the report job button that displays the dropdown.
    function reportThisJobClicked(p_oEvent) {
        YAHOO.util.Event.stopEvent(p_oEvent);

        if (isAuthenticated == "true") {
            if (jobReportedDate == null || jobReportedDate == "") {
                reportJobPanelShow();
            }
            else {
                reportJobPanelReportedShow();
            }
        }
        else {
            submitAfterLogin = 3;
            document.getElementById("destinationLink").value = reportJobHref;
            document.getElementById("loginError").innerHTML = "";
            document.getElementById("loginPanelDiv").style.display = "block";
            loginPanel.show();
        }

    }

    // Handle a registration button click on the login panel. Route the user to the original destination link
    // (rate job processor, for example) and let RTS handle registration.
    function signUpNowClicked(p_oEvent) {
        // If a form name is provided then simply submit that form.



        // Call the appropriate processor based on which button was clicked to bring up the login dialog in the first place.
        document.location.href = document.getElementById("destinationLink").value + "&rts=1";


    }

    // User clicked the login button on the panel. Send request to server for authentication.
    function logMeIn(p_oEvent) {
        var sjt_user_name = document.getElementById("SJT_USER_NAME").value;
        var sjt_password = document.getElementById("SJT_PASSWD").value;
        var xmlhttp = null;
        if (window.XMLHttpRequest) {
            // code for Firefox, Opera, IE7, etc.
            xmlhttp = new XMLHttpRequest();
        }
        else if (window.ActiveXObject) {
            // code for IE6, IE5
            xmlhttp = new ActiveXObject("Microsoft.XMLHTTP");
        }
        if (xmlhttp != null) {
            // Generate random to prevent caching.
            var d = new Date();
            var bigNumber = " " + d.getYear() + d.getDay() + d.getHours() + d.getMinutes() + d.getSeconds() + d.getMilliseconds();
            var randomNumber = (Math.floor(Math.random() * bigNumber));

            xmlhttp.onreadystatechange = logMeInResponded(xmlhttp, processLogMeInResponse);
            // Note: The dockey may not be populated. If it is populated the report and rate job information is included in the response.
            xmlhttp.open("GET", "http://seeker.dice.com/profman/servlet/ProfMan?op=2000&ajax=1&SUBMIT=1&SJT_USER_NAME=" + sjt_user_name + "&SJT_PASSWD=" + sjt_password + "&dockey=xml/c/9/c99c43e659e24135469fc9075265adc4" + "&random=" + randomNumber, true);
            xmlhttp.send(null);
        }
        else {
            //alert("Your browser does not support XMLHTTP.");
        }
    }

    // Server responded to out request for authentication. Check status codes.
    function logMeInResponded(xmlhttp, responseXmlHandler) {
        return function () {
            if (xmlhttp.readyState == 4) {
                // 4 = "loaded"
                if (xmlhttp.status == 200) {
                    // 200 = "OK"
                    //alert("xml: " + xmlhttp.responseText);
                    responseXmlHandler(xmlhttp.responseXML);
                }
                else {
                    //alert("Problem retrieving data:" + xmlhttp.statusText);
                }
            }
        }
    }

    // Process the server's response to our request for authentication.
    function processLogMeInResponse(locations) {
        var locationsVals = locations.getElementsByTagName("locations")[0];
        // If we didn't get anything back from the server,
        // exit here or risk the wrath of IE's stellar javascript error detection.
        if (locationsVals == null) {
            return;
        }
        var items = locationsVals.getElementsByTagName("item");

        for (var I = 0; I < items.length; I++) {
            var item = items[I];
            validLogin = item.getElementsByTagName("validLogin")[0].firstChild.nodeValue;

            if (validLogin == 1) {
                //var loginPanel = new YAHOO.widget.Panel("loginPanelDiv").hide();

                document.body.className = "yui-skin-sam";

                var jobRating;
                var applicationDate;
                var jobInfoPresent = item.getElementsByTagName("jobInfoPresent")[0].firstChild.nodeValue;
                if (jobInfoPresent == "1") {
                    if (item.getElementsByTagName("jobRating")[0].firstChild != null) {
                        jobRating = item.getElementsByTagName("jobRating")[0].firstChild.nodeValue;
                    }
                    if (item.getElementsByTagName("applicationDate")[0].firstChild != null) {
                        applicationDate = item.getElementsByTagName("applicationDate")[0].firstChild.nodeValue;
                    }
                    if (item.getElementsByTagName("jobReportedDate")[0].firstChild != null) {
                        jobReportedDate = item.getElementsByTagName("jobReportedDate")[0].firstChild.nodeValue;
                    }
                }




                if (submitAfterLogin == 1) {
                    // Call the appropriate processor based on which button was clicked to bring up the login dialog in the first place.
                    document.location.href = document.getElementById("destinationLink").value;
                }
                else {
                    // We're staying on this page. Update any information that needs to be updated.
                    loginPanel.hide();
                    isAuthenticated = "true";

                    document.getElementById("rating1").checked = false;
                    document.getElementById("rating2").checked = false;
                    document.getElementById("rating3").checked = false;
                    if (jobRating == 1) {
                        document.getElementById("rating1").checked = true;
                    }
                    if (jobRating == 0 || jobRating == 99) {
                        document.getElementById("rating2").checked = true;
                    }
                    if (jobRating == -1) {
                        document.getElementById("rating3").checked = true;
                    }

                    // Rate This Job was clicked. Populate the rate job dropdown with seeker's rating information for
                    // this job before showing the rating dropdown.
                    if (submitAfterLogin == 2) {
                        rateJobPanelShow();
                    }
                    else if (submitAfterLogin == 3) {
                        // Report This Job was clicked. Determine which dropdown to show based on whether this seeker has
                        // previously reported this job.
                        if (jobReportedDate == null || jobReportedDate == "") {
                            reportJobPanelShow();
                        }
                        else {
                            reportJobPanelReportedShow();
                        }
                    } else if (submitAfterLogin == 4) {
                        // Join Network was clicked.
                        document.location.href = document.getElementById("destinationLink").value;
                    }
                }


            }
            else {
                // AUthorization failed. Inform the user.
                var error = item.getElementsByTagName("error")[0].firstChild.nodeValue;
                var loginError = document.getElementById("loginError");
                loginError.innerHTML = error.replace("\n", "<br>");
            }
        }
    }

    // Catch the click event and determine if we've clicked outside of the report or rate job panels.
    // If so, hide the panel.
    document.onclick=checkClick;
    function checkClick(e) {
        var target = (e && e.target) || (event && event.srcElement);


        var obj = document.getElementById('rateJobPanel');
        checkParent(target, "rateJobPanel") ? rateJobPanelHide() : null;



        obj = document.getElementById('reportJobPanel');
        checkParent(target, "reportJobPanel") ? reportJobPanelHide() : null;



        obj = document.getElementById('reportJobPanelReported');
        checkParent(target, "reportJobPanelReported") ? reportJobPanelReportedHide() : null;



        var obj = document.getElementById('companyNetworkPanel');
        checkParent(target, "companyNetworkPanel") ? connectPanelHide() : null;

    }

    // Used by checkClick method to crawl up parental elements from the one clicked looking for the panel div name.
    function checkParent(target, panelDiv) {
        if (document.getElementById(panelDiv) != null) {
        }
        while(target.parentNode) {
            if (target == document.getElementById(panelDiv)) {
                return false;
            }
            target = target.parentNode;
        }
        return true;
    }

    // Hide the rate job dropdown.
    var rateJobPanelHide = function () {
        if (rateJobPanel != null) {
            rateJobPanel.hide();
        }
    }

    // Show the rate job dropdown.
    var rateJobPanelShow = function () {
        rateJobPanel.show();
        reportJobPanelHide();
        reportJobPanelReportedHide();
        connectPanelHide();
    }

    // Hide the network dropdown
    var connectPanelHide = function () {
        if (connectPanel != null) {
            connectPanel.hide();
        }
    }

    // Show the network dropdown.
    var connectPanelShow = function () {
        connectPanel.show();
        rateJobPanelHide();
        reportJobPanelHide();
        reportJobPanelReportedHide();
    }

    // Hide the report job dropdown.
    var reportJobPanelHide = function () {
        if (reportJobPanel != null) {
            reportJobPanel.hide();
        }
    }

    // Show the report job dropdown.
    var reportJobPanelShow = function () {
        reportJobPanel.show();
        rateJobPanelHide();
        reportJobPanelReportedHide();
        connectPanelHide();
    }

    // Hide the report job reported dropdown.
    var reportJobPanelReportedHide = function () {
        if (reportJobPanelReported != null) {
            reportJobPanelReported.hide();
        }
    }

    // Hide the report job reported dropdown.
    var reportJobPanelReportedShow = function () {
        document.getElementById('reportReportedMessage').innerHTML = "&nbsp;&nbsp;This job was reported on " + jobReportedDate;
        reportJobPanelReported.show();
        rateJobPanelHide();
        reportJobPanelHide();
        connectPanelHide();
    }
}();
</script><div id=loginPanelDiv style="display: none;"><div class=hd></div><div class=bd style=text-align:left;><div style="color:#720000; font-weight:bold; padding:5px 0; position: relative;" id=loginError></div><div class=fillForm><div style="padding-bottom:.5em; position: relative;"><label class=leftlabel>E-mail&nbsp;Address:</label><input type=text name=SJT_USER_NAME id=SJT_USER_NAME maxlength=60 value tabindex=1 class=infoRight style=width:12em;><span>&nbsp;<a href=/profman/resetPassword.jsp tabindex=3 style=font-size:10px;>Forgot your password?</a></span><div class=clear></div></div><div style=padding-bottom:.25em;><label class=leftlabel>Password:</label><input type=password name=SJT_PASSWD id=SJT_PASSWD maxlength=12 value tabindex=2 class=infoRight style=width:12em;><div class=clear></div></div><div class=buttonleft><div style=margin-left:10em;><button name=loginPanelButton id=loginPanelButton tabindex=4>Login</button></div></div></div><div id=signup><fieldset class=gradient><div class=padRow><h2 style=margin-top:0;>Not a member yet?</h2><input type=button value="Sign Up Now!" name=signUpNowPanelButton id=signUpNowPanelButton tabindex=5><span style=font-size:13px;line-height:2;font-weight:bold;>Sign up now for your FREE Dice Account!</span></div></fieldset></div></div></div></body></html>

