    var rateJobHref ="/jobsearch/servlet/JobSearch?op=306&dockey=xml/8/6/86ef0f122d742be6b730fc02fd0d5285&source=&currdoc=1&queryString=&r=" + r;
    var reportJobHref ="/jobsearch/servlet/JobSearch?op=2006&dockey=xml/8/6/86ef0f122d742be6b730fc02fd0d5285&source=&currdoc=1&queryString=&r=" + r;

    // Load up any parameters we may have been handed and build the login panel.
    YAHOO.util.Event.onContentReady("mainBody", function() {











        YAHOO.util.Event.addListener("saveJob", "click", saveJobClicked);



