                if (xmlhttp.status == 200) {
                    // 200 = "OK"
                    //alert("xml: " + xmlhttp.responseText);
                    responseXmlHandler(xmlhttp.responseXML);
                }
                else {
                    //alert("Problem retrieving data:" + xmlhttp.statusText);
                }
            }
        }
    }

