var init = function () {
    var loginPanel;
    var validLogin;
    var submitAfterLogin;
    var isAuthenticated = "false";
    var jobReportedDate = "";

    var r = Math.floor(Math.random()*10000);
    var joinNetworkHref = "/jobsearch/servlet/JobSearch?op=327&dockey=xml/7/8/7838a9c32b88f287ce8b290a3c6c5260&source=&currdoc=1&queryString=&diceID=RTX14962d&joinNetwork=1&r=" + r;
    var rateJobHref ="/jobsearch/servlet/JobSearch?op=306&dockey=xml/7/8/7838a9c32b88f287ce8b290a3c6c5260&source=&currdoc=1&queryString=&r=" + r;
    var reportJobHref ="/jobsearch/servlet/JobSearch?op=2006&dockey=xml/7/8/7838a9c32b88f287ce8b290a3c6c5260&source=&currdoc=1&queryString=&r=" + r;

    // Load up any parameters we may have been handed and build the login panel.
    YAHOO.util.Event.onContentReady("mainBody", function() {











        YAHOO.util.Event.addListener("saveJob", "click", saveJobClicked);



        YAHOO.util.Event.addListener("saveJob2", "click", saveJobClicked);



