    CB.AskInfoBox = function() {
        var _iframe;
        return {
            formOnHide: function(accountDID) {
                CB.e('semiBackground').style.visibility = 'hidden';
                CB.e('ExternalAppUserInfoRequest').style.visibility = 'hidden';
                _iframe.style.left = '-3000px';

                var task = "Canceled AskedInfo";
                CB.Tally('ExternalAskInfo', 'hideExternalAppUserInfoRequest', task);
            },

            showBox: function(applyLink, accountDID) {
                var formName = "ExternalAppUserInfoRequest";

                //var bodyBounds = Sys.UI.DomElement.getBounds(document.body);
                //_iframe.style.width = bodyBounds.width + 'px';
                //_iframe.style.height = bodyBounds.height + 'px';
                _iframe.style.width = getDocumentWidth() + 'px';
                _iframe.style.height = getDocumentHeight() + 'px';
                _iframe.style.left = '0px';

                var transBack = CB.e('semiBackground');
                transBack.style.height = getDocumentHeight() + 'px';
                transBack.style.width = getDocumentWidth() + 'px';
                transBack.style.visibility = 'visible';

                var curpos = getYScrollPos();
                CB.e(formName).style.left = (getWindowWidth() / 2) - 190 + 'px';
                CB.e(formName).style.top = (getWindowHeight() / 2) + curpos - 80 + 'px';
                CB.e(formName).style.visibility = 'visible';

                // define button url
                var hdnExternalURL = CB.e('hdnExternalURL');
                if (hdnExternalURL != null) {
                    hdnExternalURL.value = applyLink.href;
                }

                var userNameObj = CB.e('txtUserName');
                var userEmailObj = CB.e('txtUserEmail');
                // set focus
                if (userNameObj != null && userNameObj.value == "") {
                    userNameObj.focus();
                }
                else {
                    userEmailObj.focus();
                }

                var task = "AskInfo popped up";
                CB.Tally('ExternalAskInfo', 'showExternalAppUserInfoRequest', task);

                window.returnValue = false;
                return false;
            },

            initializePopups: function() {
                var formName = "ExternalAppUserInfoRequest";
                CB.e(formName).style.width = "360px";
                //CB.e(formName).style.height = "auto !important";

                var tblInput = "tblDataInput";
                CB.e(tblInput).style.width = "290px";
                CB.e(tblInput).style.height = "50px";
            },

            validateInfoAndPassData: function(accountDID) {
                var emailRE = /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/

                var spanEmailIncorrectFormat = CB.e('spanEmailIncorrectFormat');
                var spanNameMissing = CB.e('spanNameMissing');
                var spanEmailMissing = CB.e('spanEmailMissing');

                var userNameObj = CB.e('txtUserName');
                var userEmailObj = CB.e('txtUserEmail');
                if (userNameObj.value != "" && userEmailObj.value != "") {
                    // validate email structure
                    if (!emailRE.test(userEmailObj.value)) {
                        spanEmailIncorrectFormat.style.display = "";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        userEmailObj.select();
                        return false;
                    }

                    var hdnExternalURL = CB.e('hdnExternalURL');
                    if (hdnExternalURL != null) {
                        // show working message
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        var task = "Got Name and Email";
                        CB.Tally('ExternalAskInfo', 'validateInfoAndPassData', task);

                        if (hdnExternalURL.value.indexOf("ApplicantEmail") < 0) {
                            hdnExternalURL.value += "&ApplicantName=" + userNameObj.value + "&ApplicantEmail=" + userEmailObj.value;
                        }                        
                        
                        window.location.href = hdnExternalURL.value;
                    }

                    return true;
                }
                else {
                    if (userNameObj.value == "") {
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "";
                        spanEmailMissing.style.display = "none";

                        userNameObj.focus();
                    }
                    else {
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "";

                        userEmailObj.focus();
                    }

                    return false;
                }
            },

            validateInfoAndPassDataEmailOnly: function(accountDID) {
                var emailRE = /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/

                var spanEmailIncorrectFormat = CB.e('spanEmailIncorrectFormat');
                var spanNameMissing = CB.e('spanNameMissing');
                var spanEmailMissing = CB.e('spanEmailMissing');

                var userEmailObj = CB.e('txtUserEmail')
                if (userEmailObj.value != "") {
                    // validate email structure
                    if (!emailRE.test(userEmailObj.value)) {
                        spanEmailIncorrectFormat.style.display = "";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        userEmailObj.select();
                        return false;
                    }

                    var hdnExternalURL = CB.e('hdnExternalURL');
                    if (hdnExternalURL != null) {
                        // show working message
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        var task = "Got Email";
                        CB.Tally('ExternalAskInfo', 'validateInfoAndPassDataEmailOnly', task);

                        if (hdnExternalURL.value.indexOf("ApplicantEmail") < 0) {
                            hdnExternalURL.value += "&ApplicantName=&ApplicantEmail=" + userEmailObj.value;
                        }                        
                        
                        window.location.href = hdnExternalURL.value;
                    }

                    return true;
                }
                else {
                    spanEmailIncorrectFormat.style.display = "none";
                    spanNameMissing.style.display = "none";
                    spanEmailMissing.style.display = "";

                    userEmailObj.focus();
                    return false;
                }
            },

            validateInfoAndPassDataEmailOptional: function(accountDID) {
                var emailRE = /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/

                var spanEmailIncorrectFormat = CB.e('spanEmailIncorrectFormat');
                var spanNameMissing = CB.e('spanNameMissing');
                var spanEmailMissing = CB.e('spanEmailMissing');

                var hdnExternalURL = CB.e('hdnExternalURL');

                var userEmailObj = CB.e('txtUserEmail');
                if (userEmailObj.value != "") {
                    // validate email structure
                    if (!emailRE.test(userEmailObj.value)) {
                        spanEmailIncorrectFormat.style.display = "";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        userEmailObj.select();
                        return false;
                    }

                    if (hdnExternalURL != null) {
                        // show working message
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        var task = "Got Email";
                        CB.Tally('ExternalAskInfo', 'validateInfoAndPassDataEmailOptional', task);

                        if (hdnExternalURL.value.indexOf("ApplicantEmail") < 0) {
                            hdnExternalURL.value += "&ApplicantName=&ApplicantEmail=" + userEmailObj.value;
                        }                        
                        
                        window.location.href = hdnExternalURL.value;
                    }
                }
                else {
                    // user did not give an email
                    if (hdnExternalURL != null) {
                        // show working message
                        spanEmailIncorrectFormat.style.display = "none";
                        spanNameMissing.style.display = "none";
                        spanEmailMissing.style.display = "none";

                        var task = "Got No Email";
                        CB.Tally('ExternalAskInfo', 'validateInfoAndPassDataEmailOptional', task);
                        hdnExternalURL.value += "&ApplicantName=&ApplicantEmail=";
                        window.location.href = hdnExternalURL.value;
                    }
                }

                return true;
            },

            tallyApplyNowClick: function(accountDID) {
                var task = "Apply Now Clicked";
                CB.Tally('ExternalAskInfo', 'tallyApplyNowClick', task);
            },

            onWindowLoad: function(evt) {
                // setup the iframe
                _iframe = document.createElement("iframe");
                _iframe.id = 'EXpopupIframe';
                _iframe.style.position = 'absolute';
                _iframe.style.background = 0;
                _iframe.style.filter = 'chroma(color=#FFFFFF)';
